import React from 'react'
import './shop.style.css'
import { Route } from 'react-router-dom'
import { firestore, convertCollectionsSnapShotToMap } from '../../firebase/firebase.utils'
import { createStructuredSelector } from 'reselect';
import CollectionViews from '../../components/collection-overview/collection-overview.component'
import CollectionPage from '../collection/collection.component'
import { selectCollectionForPreview } from '../../redux/shop/shop.selectors'

import { connect } from 'react-redux'
import { updateCollections } from '../../redux/shop/shop.actions'
import WithSpinner from '../../components/with-spinner/with-spinner.component'

const CollectionViewsWithSpinner = WithSpinner(CollectionViews);
const CollectionPageWithSpinner = WithSpinner(CollectionPage)

class ShopPage extends React.Component {
    unsubscribeFromSnapshot = null;

    componentDidMount() {
        const collectionRef = firestore.collection('collections');
        const { updateCollections } = this.props

        this.unsubscribeFromSnapshot = collectionRef.onSnapshot(snapshot => {
            const collectionMap = convertCollectionsSnapShotToMap(snapshot)
            updateCollections(collectionMap);
            this.setState({ loading: false });
        });

    }

    componentWillUnmount() { }

    render() {

        const { match, isLoading } = this.props
        return (
            <div className='collection-page'>
                <Route exact path={`${match.path}`}
                    render={(props) =>
                        <CollectionViewsWithSpinner isLoading={isLoading} {...props} />} />
                <Route exact path={`${match.path}/:collectionId`}
                    render={(props) =>
                        <CollectionPageWithSpinner isLoading={isLoading} {...props} />} />
            </div>
        )
    }
}
const mapDispatchToProps = dispatch => ({
    updateCollections: collectionsMap => dispatch(updateCollections(collectionsMap))
})

const mapStateToProps = ({ collections }) => ({

    collections: collections
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
