import React from 'react'
import './shop.style.css'
import { Route } from 'react-router-dom'
import { firestore, convertCollectionsSnapShotToMap } from '../../firebase/firebase.utils'

import CollectionViews from '../../components/collection-overview/collection-overview.component'
import CollectionPage from '../collection/collection.component'

import { connect } from 'react-redux'
import { addCollections } from '../../redux/shop/shop.actions'

class ShopPage extends React.Component {
    unsubscribeFromSnapshot = null;
    constructor(props) {
        super(props);
        const { addCollections } = this.props

    }


    componentDidMount() {
        const collectionRef = firestore.collection('collections');


        collectionRef.onSnapshot(snapshot => {
            const collectionMap = convertCollectionsSnapShotToMap(snapshot)
            addCollections(collectionMap)();
        });

    }

    componentWillUnmount() { }

    render() {
        const { match } = this.props
        return (
            <div className='collection-page'>
                <Route exact path={`${match.path}`} component={CollectionViews} />
                <Route exact path={`${match.path}/:collectionId`} component={CollectionPage} />
            </div>
        )
    }
}
const mapDispatchToProps = dispatch => ({
    addCollections: collections => dispatch(addCollections(collections))
})

export default connect(mapDispatchToProps)(ShopPage);
